import React, { useEffect, useState } from 'react'
import {API_URL, API_KEY, IMAGE_URL} from '../../Config'
import MainImage from '../LandingPage/Sections/MainImage'
import { Descriptions, Button, Row } from 'antd'
import GridCard from '../LandingPage/Sections/GridCard'

function MovieDetailPage(props) {

    const [Movie, setMovie] = useState([])
    const [Crews, setCrews] = useState([])


    useEffect(() => {

        const movieId = props.match.params.movieId

        fetch(`${API_URL}movie/${movieId}?api_key=${API_KEY}&language=en-US`)
        .then(response => response.json())
        .then(response => {
            setMovie(response)
            fetch(`${API_URL}movie/${movieId}/credits?api_key=${API_KEY}`)
            .then(reponse => response.json)
            .then(reponse => {
                setCrews(response.cast)

            })
        })
    }, [])


    return (
        <div>
            {/* Main Image */}
            {Movie && 
            <MainImage image={`${IMAGE_URL}w1280${Movies.backdrop_path && Movies.backdrop_path}`} 
            title={Movie.original_title} text={Movies.overview} />
            }

        {/* Body */}
        <div style={{ width: '85%', margin: '1rem auto'}}>
            <div style={{ display: 'flex', justifyContent: 'flex-end'}}>
                <Button> Add to Favorite</Button>
            </div>

        {/* Movie Info Table*/}
        <Descriptions title="Movie Info" bordered>
            <Descriptions.Item label="Title">{MovieDetailPage.original_title}</Descriptions.Item>
            <Descriptions.Item label="release_date">{MovieDetailPage.release_date}</Descriptions.Item>
            <Descriptions.Item label="revenue">{MovieDetailPage.revenue}e</Descriptions.Item>
            <Descriptions.Item label="runtime">{MovieDetailPage.runtime}</Descriptions.Item>
            <Descriptions.Item label="vote_average" span={2}>
                {MovieDetailPage.vote_average}</Descriptions.Item>
            <Descriptions.Item label="vote_count">{MovieDetailPage.vote_count}</Descriptions.Item>
            <Descriptions.Item label="status">{MovieDetailPage.status}</Descriptions.Item>
            <Descriptions.Item label="popularity">{MovieDetailPage.popularity}</Descriptions.Item>
        </Descriptions>


            <div style={{ display: 'flex', justifyContent: 'center'}}>
                <Button> Toggle Actor View</Button>
            </div>

        {/* Gridcard for crews */}
            <Row gutter={[16,16]}>
            {Crews && Crews.map((crew, index) => (
                <React.Fragment key={index}>
                    {crew.profile_path &&
                    <GridCard
                    actor image={`{IMAGE_URL}w500${crew.profile.path}`}
                     />
                    }
                </React.Fragment>

            ))}  
            </Row>


        </div>
        </div>
    )
}

export default MovieDetailPage;
